sys_script_include.script:
var UIActionHelperBase = Class.create();
UIActionHelperBase.prototype = Object.extendsObject(Core, {

    /**
     * 
     * @param {GlideRecord} grFile current record to be scanned
     * @returns boolean - true if file can be scanned
     */
    canScanGr: function(grFile) {
        if(!grFile || !grFile.isValidRecord()) return false;
        return this._canScanTable(grFile.getTableName());
    },


    type: 'UIActionHelperBase'
});
